import React from 'react';
import PropTypes from 'prop-types';

import { Typography, Grid, Switch } from '@material-ui/core';


export default class Info extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      leftAngle: undefined,
      rightAngle: undefined,
      action: false
    };

    this.setAction = this.setAction.bind(this);
  }

  update(side, angle) {
    if (side === 0) {
      this.setState({ leftAngle: angle });
    } else {
      this.setState({ rightAngle: angle });
    }
  }

  getState() {
    const data = {};
    data.leftAngle = this.state.leftAngle;
    data.rightAngle = this.state.rightAngle;
    return data;
  }

  updateState(data) {
    this.setState({
      leftAngle: data && (data.leftAngle || (data.left && data.left.angle)),
      rightAngle: data && (data.rightAngle || (data.right && data.right.angle)),
      action: false
    });
  }

  setAction(e) {
    this.props.onSideChange(e.target.checked);
    this.setState({ action: e.target.checked });
  }

  resetInfo() {
    this.setState({
      leftAngle: undefined,
      rightAngle: undefined,
      action: false
    });
  }

  colorValidationAngle( value) {
    const diff = this.props.threshold.value - value;
    return diff < 0 ? 'error' : 'inherit';
  }

  render() {
    return (
      <Grid container className="switch-container" alignItems='center' justify='center'>
        <Grid item xs={4} >
          <Typography color={this.colorValidationAngle(this.state.leftAngle)} align='center' >
            {this.state.leftAngle || '-'}°
          </Typography>
          <Typography align='center' variant="h5" component="h3">
            Left
          </Typography>
        </Grid>

        <Grid item>
          <Switch color="primary" onChange={this.setAction} checked={this.state.action} />
        </Grid>

        <Grid item xs={4}>
          <Typography color={this.colorValidationAngle(this.state.rightAngle)} align='center'>
            {this.state.rightAngle || '-'}°
          </Typography>
          <Typography align='center' variant="h5" component="h3">
            Right
          </Typography>
        </Grid>
      </Grid>
    );
  }
}

Info.propTypes = {
  onSideChange: PropTypes.func.isRequired,
  threshold: PropTypes.object.isRequired
};
